d.SetText("Main text.", "More text.\nSupports &lt;A HREF=\"link data\"&gt;links&lt;/A&gt; if you subscribe to HyperlinkClicked event.");
d.SetButtons("1 OK|2 Cancel|3 Custom|4 Custom2");
