using(WinHook.ThreadCbt(x =>
{
	Print(x.code);
	switch(x.code) {
	case HookData.CbtEvent.ACTIVATE:
		Print(x.ActivationInfo(out _, out _));
		break;
	case HookData.CbtEvent.CREATEWND:
		Print(x.CreationInfo(out var c, out _), c->x, c->lpszName);
